mapper.select:
SELECT ${sqlMap.column.toSql()},
	(select t.user_name from js_sys_user t where t.login_code=a.stu)stuname,
	(select t.user_name from js_sys_user t where t.login_code=a.com)comname
FROM ${sqlMap.table.toSql()}

	${sqlMap.where.toSql()}

		and a.stu in (select t.login_code from js_sys_user t where t.user_name like '%'||#{stuname}||'%')


		and a.com in (select t.login_code from js_sys_user t where t.user_name like '%'||#{comname}||'%')


ORDER BY ${sqlMap.order.toSql()}
